mapper.select:
select u.UserID, p.PropertyID, p.EstateName, p.RentalType, p.PropertySource, p.PropertyStatus, p.IsHide, p.ApprovalStatus, p.PaymentStatus, p.IsAdminOffLine, p.CreateTime, p.UpdateTime, p.ApprovalTime, p.PublicTime from property p left JOIN property_userinfo u on p.PropertyID = u.PropertyID and u.IsDelete = '0' where p.IsDelete = '0'
AND p.PaymentStatus = #{paymentStatus}
AND p.ApprovalStatus = #{approvalStatus}
AND u.PropertyID = #{propertyId}
AND p.EstateName like concat('%',#{estateName,jdbcType=VARCHAR},'%')
AND p.PropertySource = #{propertySource}
AND p.PropertyStatus = #{propertyStatus}
AND p.IsHide = #{isHide}
AND p.PropertyStatus in
#{propertyStatus}
and p.CreateTime >= STR_TO_DATE(#{beginTime} '00:00:00', '%Y-%m-%d %H:%i:%s')
and p.CreateTime <= STR_TO_DATE(#{endTime} '23:59:59', '%Y-%m-%d %H:%i:%s')
and p.UpdateTime >= STR_TO_DATE(#{beginUpdateTime} '00:00:00', '%Y-%m-%d %H:%i:%s')
and p.UpdateTime <= STR_TO_DATE(#{endUpdateTime} '23:59:59', '%Y-%m-%d %H:%i:%s')
and p.PublicTime >= STR_TO_DATE(#{beginPublictime} '00:00:00', '%Y-%m-%d %H:%i:%s')
and p.PublicTime <= STR_TO_DATE(#{endPublictime} '23:59:59', '%Y-%m-%d %H:%i:%s')
and p.ApprovalTime >= STR_TO_DATE(#{beginApprovalTime} '00:00:00', '%Y-%m-%d %H:%i:%s')
and p.ApprovalTime <= STR_TO_DATE(#{endApprovalTime} '23:59:59', '%Y-%m-%d %H:%i:%s')
AND p.IsAdminOffLine = #{isadminoffline}
AND p.RentalType = #{rentaltype}
ORDER BY p.CreateTime desc

select PropertyID ,EstateName,RentalType , PropertySource,IsVerifyLabel,PublicTime from property
where IsDelete=0 and (IsVerify=1 or IsVerifyImage=1 ) and PaymentStatus=1

    and PropertyID=#{propertyid}


    and PropertySource=#{propertysource}


    and IsVerifyLabel=#{isverifylabel}


     and PublicTime  >=  #{startTime}  


     and PublicTime <=  #{endTime}

select PropertyID ,EstateName,RentalType , PropertySource,ApprovalStatus,PublicTime from property
where IsDelete=0 and EstateID=0 and PaymentStatus=1

    and PropertyID=#{propertyid}


    and PropertySource=#{propertysource}


    and ApprovalStatus=#{approvalstatus}


     and PublicTime  >=  #{startTime}  


     and PublicTime <=  #{endTime}
